import PropTypes from 'prop-types';
import styled from "@emotion/styled";

const ScoreBoard = styled.div`
	display: grid;
	grid-template-columns: 50% 50%;
	grid-template-rows: repeat(3, auto);

	.team1, .team2 {
		grid-column-start: 0;
		width: 100%;
		text-align: center;

    .score{
      min-height: 150px;
    }

		.scoreButtons {
      width: 100%;
      display: grid;
      grid-template-columns: repeat(5, 19.5%);
      grid-template-rows: repeat(3, 80px);
      grid-gap: 3px;
      margin: 0 auto;

      .scoreButton{
        padding: 0;
        grid-column-start: 2;
        grid-column-end: 5;
      }
		}
	}
`;

let TEAMONESCORE = 0;
let TEAMTWOSCORE = 0;

function teamOne3() {
    TEAMONESCORE = TEAMONESCORE + 3;
    document.getElementById('TeamOneScore').innerHTML = TEAMONESCORE;
}
function teamOne2() {
    TEAMONESCORE = TEAMONESCORE + 2;
    document.getElementById('TeamOneScore').innerHTML = TEAMONESCORE;
}
function teamOne1() {
    TEAMONESCORE = TEAMONESCORE + 1;
    document.getElementById('TeamOneScore').innerHTML = TEAMONESCORE;
}

function Home() {
	return (
		<>
			<h1>Basketball Time!</h1>
			<ScoreBoard>
				<div className="team1">
					<h3 className="name">Tom</h3>
					<h2 className="score" id="TeamOneScore"> 0 </h2>

					<div className="scoreButtons">
						<button className="scoreButton" onClick={ teamOne3 }>3</button>
						<button className="scoreButton" onClick={ teamOne2 }>2</button>
						<button className="scoreButton" onClick={ teamOne1 }>1</button>
					</div>
				</div>

				<div className="team2">
					<h3 className="name">Liam</h3>
          <h2 className="score"> {TEAMTWOSCORE} </h2>

					<div className="scoreButtons">
						<button className="scoreButton">3</button>
						<button className="scoreButton">2</button>
						<button className="scoreButton">1</button>
					</div>
				</div>
			</ScoreBoard>
		</>
	);
}

export default Home;
